-- PRAGMA foreign_keys=OFF;
BEGIN;
CREATE TABLE "schema_migrations" ("version" text NOT NULL);
CREATE TABLE "movies" ("id" SERIAL PRIMARY KEY NOT NULL, "title" text, "director" text, "runtime_in_minutes" integer, "description" text, "poster_image_url" text, "release_date" timestamp, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "image" text);
CREATE TABLE "users" ("id" SERIAL PRIMARY KEY NOT NULL, "email" text, "password_digest" text, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "firstname" text, "lastname" text, "admin" boolean);
CREATE TABLE "reviews" ("id" SERIAL PRIMARY KEY NOT NULL, "user_id" integer, "movie_id" integer, "text" text, "rating_out_of_ten" integer, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_reviews_on_user_id" ON "reviews" ("user_id");
CREATE INDEX "index_reviews_on_movie_id" ON "reviews" ("movie_id");
COMMIT;
